import { useEffect, useRef } from "react";
function useUpdateEffect(callback, deps) {
  var renderCycleRef = useRef(false);
  var effectCycleRef = useRef(false);
  useEffect(function () {
    var mounted = renderCycleRef.current;
    var run = mounted && effectCycleRef.current;
    if (run) {
      return callback();
    }
    effectCycleRef.current = true;
  }, deps);
  useEffect(function () {
    renderCycleRef.current = true;
    return function () {
      renderCycleRef.current = false;
    };
  }, []);
}
export { useUpdateEffect };
